import React, { useState, useEffect, useRef } from "react";
import {
  Dialog,
  DialogContent,
  DialogTitle,
  Typography,
  DialogActions,
  Button,
  Grid,
  Switch,
  responsiveFontSizes,
  TextField,
  useFormControl,
} from "@material-ui/core";
import { useSelector, useDispatch } from "react-redux";
import { updateDetailSwitch, detailUpdateChange } from "../../redux/actions";
import patchSwitch from "../../redux/patchData";
import { fetchDetail } from "../../redux/fetchData";

//form
import InputTextField from "./Form/InputTextField";

function DialogEditUsers(props) {
  const { history, match } = props;
  let params = match.params.id;

  //redux
  let dispatch = useDispatch();

  //reducer fetching data
  const reducer = useSelector((state) => state);
  const res = reducer.detailReducer;

  //state init
  let TextfieldRef = useRef(null);
  const [toggle, setToggle] = useState({ is_active: true });

  useEffect(() => {
    dispatch(fetchDetail(params));
  }, []);

  const handleCancel = () => {
    history.goBack();
  };

  const handleChangeName = (event) => {
    const { name, value } = event.target;
    console.log("hallo update name", event.target);
    dispatch(detailUpdateChange(value));
  };

  const handleChangeComment = (event) => {
    console.log("hallo update comment", event.target);
    dispatch(detailUpdateChange(event.target.value));
  };
  const handleChangeSwitch = (event) => {
    console.log("is aktive =>", event.target.checked);
    // setToggle({
    //   ...toggle,
    //   is_active: !toggle.is_active,
    // });
  };
  // const handleSubmit = (event) => {
  //   event.preventDefault();
  //   let data = res.data;
  //   data = {
  //     ...res.data,
  //     name: event.target[0].value,
  //     comment: event.target[2].value,
  //     is_active: event.target[4].checked,
  //   };
  //   console.log("value submitan", res.data, data);
  // };

  return (
    <Dialog open={true} aria-labelledby="form-dialog-edit-users" fullWidth>
      <form noValidate autoComplete="off">
        <DialogTitle>
          <Typography component="span" variant="h4">
            <b> Edit Data</b>
          </Typography>
        </DialogTitle>
        <DialogContent>
          <Grid container spacing={2}>
            <Grid item xs={12}>
              <Typography component="span">Name</Typography>
              <TextField
                onChange={handleChangeName}
                name="name"
                // pending={res.pending}
                variant="outlined"
                fullWidth
                value={res.data.name}
              />
            </Grid>
            <Grid item xs={12}>
              <Typography component="span">Comment</Typography>
              <TextField
                onChange={handleChangeComment}
                name="comment"
                fullWidth
                multiline
                rows={4}
                // pending={res.pending}
                variant="outlined"
                // keys={`${Math.floor(Math.random() * 1000)}-min`}
                value={res.data.comment}
              />
            </Grid>
            <Grid item xs={12}>
              <Typography component="span">Is active</Typography>
              <Switch
                name="is_active"
                disabled={res.pending}
                checked={
                  !res.pending
                    ? res.data.is_active === toggle.is_active
                      ? true
                      : false
                    : false
                }
                onChange={handleChangeSwitch}
              />
            </Grid>
          </Grid>
        </DialogContent>
        <DialogActions>
          <Button color="primary" onClick={handleCancel}>
            Cancel
          </Button>
          <Button color="primary" type="submit">
            Submit
          </Button>
        </DialogActions>
      </form>
    </Dialog>
  );
}

export default DialogEditUsers;
