import React, { useState, useEffect, useRef } from "react";
import {
  Dialog,
  DialogContent,
  DialogTitle,
  Typography,
  DialogActions,
  Button,
  Grid,
  Switch,
  responsiveFontSizes,
  TextField,
  useFormControl,
} from "@material-ui/core";
import { useSelector, useDispatch } from "react-redux";
import { updateDetailSwitch, detailUpdateChange } from "../../redux/actions";
// import patchSwitch from "../../redux/patchData";
import { fetchDetail } from "../../redux/fetchData";

//form
import InputTextField from "./Form/InputTextField";

function DialogEditUsers(props) {
  const { history, match } = props;
  let params = match.params.id;

  //redux
  let dispatch = useDispatch();

  //reducer fetching data
  const reducer = useSelector((state) => state);
  const res = reducer.detailReducer;

  //state init
  let TextfieldRef = useRef(null);
  const [toggle, setToggle] = useState({ is_active: true });

  useEffect(() => {
    dispatch(fetchDetail(params));
  }, []);

  const handleCancel = () => {
    history.goBack();
  };

  const handleChange = (event) => {
    const { name, value } = event.target;
    console.log("hallo update name", event.target);
    const payload = {
      [name]: value,
    };
    dispatch(detailUpdateChange(payload));
  };

  const handleChangeSwitch = (statusPast) => (event) => {
    setToggle({
      ...toggle,
      is_active: event.target.checked,
    });
    console.log("is aktive =>", event.target.checked, statusPast);
    // dispatch(updateDetailSwitch(event.target.checked));
  };

  return (
    <Grid container spacing={2}>
            <Grid item xs={12}>
              <Typography component="span">Name</Typography>
              <TextField
                onChange={handleChange}
                name="name"
                pending={res.pending}
                variant="outlined"
                fullWidth
                value={res.data.name}
              />
            </Grid>
            <Grid item xs={12}>
              <Typography component="span">Comment</Typography>
              <TextField
                onChange={handleChange}
                name="comment"
                fullWidth
                multiline
                rows={4}
                pending={res.pending}
                variant="outlined"
                value={res.data.comment}
              />
            </Grid>
            <Grid item xs={12}>
              <Typography component="span">Is active</Typography>
              <Switch
                name="is_active"
                disabled={res.pending}
                checked={
                  !res.pending
                    ? res.data.is_active === toggle.is_active
                      ? true
                      : false
                    : false
                }
                onChange={handleChangeSwitch(res.data.is_active)}
              />
            </Grid>
          </Grid>
  );
}

export default DialogEditUsers;
