import Axios from "axios";
import { patchDataPending, patchDataSuccess, patchDataError } from "./actions";

const patchSwitch = (id, data) => {
  console.log(id, data);
  const body = {
    data: {
      is_active: false,
    },
  };
  return (dispatch) => {
    dispatch(patchDataPending());
    Axios.patch(
      `https://api.jokolodang.com/api/v1/payments/${id}/activate`,
      body
    )
      .then((res) => {
        dispatch(patchDataSuccess(res));
        console.log("ini respon", res);

        return res.data;
      })
      .catch((error) => {
        dispatch(patchDataError(error));
      });
  };
};

export default patchSwitch;
