import React, { useState, useEffect, useRef } from "react";
import {
  Dialog,
  DialogContent,
  DialogTitle,
  Typography,
  DialogActions,
  Button,
  Grid,
  Switch,
  responsiveFontSizes,
  useFormControl,
} from "@material-ui/core";
import { useSelector, useDispatch } from "react-redux";
import { updateDetailSwitch, detailUpdateChange } from "../../redux/actions";
import patchSwitch from "../../redux/patchData";
import { fetchDetail } from "../../redux/fetchData";

//form
import InputTextField from "./Form/InputTextField";

function DialogEditUsers(props) {
  const { history, match } = props;
  let params = match.params.id;

  //redux
  let dispatch = useDispatch();

  
  //reducer fetching data
  const reducer = useSelector((state) => state);
  const res = reducer.detailReducer;
  
  // ref init
  const dialogRef = useRef(null);
  const inputTextNameRef = useRef(null);

  //state init
  const [toggle, setToggle] = useState({ is_active: true });

  useEffect(() => {
    dispatch(fetchDetail(params));
  }, []);

  const handleCancel = () => {
    history.goBack();
  };

  const handleChangeName = (event) => {
    // event.preventDefault();
    console.log("hallo update name");
    const data = {
      ...res.data,
      name: event.target.value,
    };
    dispatch(detailUpdateChange(data));
  };

  const handleChangeComment = () => {
    console.log("hallo update comment");
  };
  const handleChangeSwitch = (event) => {
    console.log("is aktive =>", event.target.checked);
    // setToggle({
    //   ...toggle,
    //   is_active: !toggle.is_active,
    // });
  };
  const handleSubmit = (event) => {
    event.preventDefault();
    let data = res.data;
    data = {
      ...res.data,
      name: event.target[0].value,
      comment: event.target[2].value,
      is_active: event.target[4].checked,
    };
    console.log("value submitan", res.data, data);
  };

  return (
    <Dialog
      open={true}
      aria-labelledby="form-dialog-edit-users"
      fullWidth
      ref={dialogRef}
    >
      {/* <form onSubmit={handleSubmit}> */}
      <DialogTitle>
        <Typography component="span" variant="h4">
          <b> Edit Data</b>
        </Typography>
      </DialogTitle>
      <DialogContent>
        <Grid container spacing={2}>
          <Grid item xs={12}>
            <Typography component="span">Name</Typography>
            <InputTextField
              changeValue={handleChangeName}
              name="username"
              fullWidth
              keys={`${Math.floor(Math.random() * 1000)}-min`}
              value={res.data.name}
            />
          </Grid>
          <Grid item xs={12}>
            <Typography component="span">Comment</Typography>
            <InputTextField
              changeValue={handleChangeComment}
              name="comment"
              fullWidth
              multiline
              rows={4}
              keys={`${Math.floor(Math.random() * 1000)}-min`}
              value={res.pending ? "loading..." : res.data.comment}
            />
          </Grid>
          <Grid item xs={12}>
            <Typography component="span">Is active</Typography>
            <Switch
              name="is_active"
              disabled={res.pending}
              checked={
                !res.pending
                  ? res.data.is_active === toggle.is_active
                    ? true
                    : false
                  : false
              }
              onChange={handleChangeSwitch}
            />
          </Grid>
        </Grid>
      </DialogContent>
      <DialogActions>
        <Button color="primary" onClick={handleCancel}>
          Cancel
        </Button>
        <Button color="primary" type="submit">
          Submit
        </Button>
      </DialogActions>
      {/* </form> */}
    </Dialog>
  );
}

export default DialogEditUsers;
