import React, { useState, useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
// import { useHistory } from "react-router-dom";
import { fetchdData, fetchDetail } from "../redux/fetchData";
import patchSwitch from "../redux/patchData";

import {
  Grid,
  Typography,
  Table,
  TableContainer,
  TableHead,
  TableBody,
  TableCell,
  Switch,
  TableRow,
  Button,
} from "@material-ui/core/";

function Home() {
  let dispatch = useDispatch();
  // let history = useHistory();
  useEffect(() => {
    dispatch(fetchdData());
  }, []);

  // state init
  //   const [active, setactive] = useState({ statusSwitch: false });

  //reducer fetching data
  const state = useSelector((state) => state);
  const data = state.dataReducer;

  const handleChange = (id, status) => (props) => {
    console.log(id, status);
    dispatch(patchSwitch(id, status));
  };

  const handleDelete = () => {
    return null;
  };

  const handleClickName = (id) => (event) => {
    // history.replace('/users/id')
    dispatch(fetchDetail(id));
    console.log("this is working", id);
  };

  console.log("Data reducer ==>", data);
  return (
    <Grid container item xs={12} justify="center" alignItems="center">
      <Grid item xs={10}>
        <TableContainer>
          <Table aria-label="simple-table">
            <TableHead>
              <TableRow>
                <TableCell>id</TableCell>
                <TableCell>name</TableCell>
                <TableCell>active</TableCell>
                <TableCell>delete</TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {data.pending === true ? (
                <TableRow>
                  <TableCell>
                    <Typography component="span" variant="h3">
                      Loading...
                    </Typography>
                  </TableCell>
                  <TableCell></TableCell>
                  <TableCell></TableCell>
                  <TableCell></TableCell>
                </TableRow>
              ) : (
                data.data.map((value, index) => (
                  <TableRow key={index}>
                    <TableCell>{value.id}</TableCell>
                    <TableCell>
                      <Typography
                        component="span"
                        variant="inherit"
                        onClick={handleClickName(value.id)}
                        style={{
                          textDecoration: "underline",
                          cursor: "pointer",
                        }}
                      >
                        {value.name}
                      </Typography>
                    </TableCell>
                    <TableCell>
                      <Switch
                        value={value.is_active}
                        checked={value.is_active}
                        onChange={handleChange(value.id, value.is_active)}
                        name="checkedA"
                      />
                    </TableCell>
                    <TableCell>
                      <Button onClick={handleDelete}>Delete</Button>
                    </TableCell>
                  </TableRow>
                ))
              )}
            </TableBody>
          </Table>
        </TableContainer>
      </Grid>
    </Grid>
  );
}

export default Home;
