import Axios from "axios";
import { patchDataPending, patchDataSuccess, patchDataError } from "./actions";

const patchSwitch = (id, data) => {
  console.log(id, data);

  return (dispatch) => {
    dispatch(patchDataPending());
    Axios.patch(`https://api.jokolodang.com/api/v1/payments/${id}/activate`, {
      "is_active": data,
    })
      .then((res) => {
        dispatch(patchDataSuccess(res.data));
        return res.data;
      })
      .catch((error) => {
        dispatch(patchDataError(error));
      });
  };
};

export default patchSwitch;
