//action fetch data
export const FETCH_DATA_PENDING = "FETCH_DATA_PENDING";
export const FETCH_DATA_SUCCESS = "FETCH_DATA_SUCCESS";
export const FETCH_DATA_ERROR = "FETCH_DATA_ERROR";
//detail
export const FETCH_DETAIL_SUCCESS = "FETCH_DETAIL_SUCCESS";
export const FETCH_DETAIL_PENDING = "FETCH_DETAIL_PENDING";

//action patch data
export const PATCH_DATA_PENDING = "PATCH_DATA_PENDING";
export const PATCH_DATA_SUCCESS = "PATCH_DATA_SUCCESS";
export const PATCH_DATA_ERROR = "PATCH_DATA_ERROR";

export const fetchDataPending = (pendding) => {
  //   { id, nama, harga } = data;
  return (dispatch, getState) => {
    dispatch({
      type: FETCH_DATA_PENDING,
    });
  };
};

export const fetchDataSuccess = (data) => {
  //   { id, nama, harga } = data;
  return (dispatch, getState) => {
    dispatch({
      type: FETCH_DATA_SUCCESS,
      data: data,
    });
  };
};

export const fetchDetailPending = (pendding) => {
  //   { id, nama, harga } = data;
  return (dispatch, getState) => {
    dispatch({
      type: FETCH_DETAIL_PENDING,
    });
  };
};

export const fetchDetailSuccess = (data) => {
  //   { id, nama, harga } = data;
  return (dispatch, getState) => {
    dispatch({
      type: FETCH_DETAIL_SUCCESS,
      data: data,
    });
  };
};

export const fetchDataError = (error) => {
  //   { id, nama, harga } = data;
  return (dispatch, getState) => {
    dispatch({
      type: FETCH_DATA_ERROR,
      data: error,
    });
  };
};

export const patchDataPending = (pendding) => {
  //   { id, nama, harga } = data;
  return (dispatch, getState) => {
    dispatch({
      type: PATCH_DATA_PENDING,
    });
  };
};

export const patchDataSuccess = (data) => {
  //   { id, nama, harga } = data;
  return (dispatch, getState) => {
    dispatch({
      type: PATCH_DATA_SUCCESS,
      data: data,
    });
  };
};

export const patchDataError = (error) => {
  //   { id, nama, harga } = data;
  return (dispatch, getState) => {
    dispatch({
      type: PATCH_DATA_ERROR,
      data: error,
    });
  };
};
