import Axios from "axios";
import {
  fetchDataPending,
  fetchDataSuccess,
  fetchDataError,
  patchDataPending,
  patchDataSuccess,
  patchDataError,
} from "./actions";

const fetchdData = () => {
  return (dispatch) => {
    dispatch(patchDataPending());
    Axios.get("https://api.jokolodang.com/api/v1/payments")
      .then((res) => {
        dispatch(fetchDataSuccess(res.data));
        return res.data;
      })
      .catch((error) => {
        dispatch(fetchDataError(error));
      });
  };
};

const patchSwitch = (id) => {
  return (dispatch) => {
    dispatch(fetchDataPending());
    Axios.patch(`https://api.jokolodang.com/api/v1/payments/${id}/activate`)
      .then((res) => {
        dispatch(patchDataSuccess(res.data));
        return res.data;
      })
      .catch((error) => {
        dispatch(patchDataError(error));
      });
  };
};

export default fetchdData;
