import { combineReducers } from "redux";
import {
  FETCH_DATA_PENDING,
  FETCH_DATA_SUCCESS,
  FETCH_DATA_ERROR,
} from "./actions";

//initial state
const initialState = {
  pending: false,
  data: [
    { id: "12123322", nama: "asep imam nawawi" },
    { id: "12123322", nama: "asep imam nawawi" },
    { id: "12123322", nama: "asep imam nawawi" },
  ],
  error: null,
};

function dataReducer(state = initialState, action) {
  console.log(state, action);

  switch (action.type) {
    case FETCH_DATA_PENDING:
      // return {
      //   ...state,
      //   pending: true,
      // };
      console.log("status pending true");
      break;

    case FETCH_DATA_SUCCESS:
      return {
        ...state,
        pending: false,
        data: action.payload,
      };
    // console.log("status data success");
    // break;

    case FETCH_DATA_ERROR:
      // return {
      //   ...state,
      //   pending: false,
      //   error: action.error,
      // };
      console.log("status data error");
      break;

    default:
      return state;
  }
}

function whitelist(state = initialState, action) {
  switch (action.type) {
    case "ADD_WHITE_LIST":
      return {
        ...state,
        data: action.payload,
      };

    case "REMOVE_WHITE_LIST":
      return state.filter((item) => item.id !== action.id);

    default:
      // the dispatched action is not in this reducer, return the state unchanged
      return state;
  }
}
const rootReducers = combineReducers({
  whitelist: whitelist,
  data: dataReducer,
});

export default rootReducers;
