import Axios from "axios";
import { patchDataPending, patchDataSuccess, patchDataError } from "./actions";

const patchSwitch = (id, data) => {
  console.log(id, data);
  let datas = data ? 0 : 1;

  const urlA = `https://api.jokolodang.com/api/v1/payments/${id}/activate`;
  const urlD = `https://api.jokolodang.com/api/v1/payments/${id}/deactivate`;
  return (dispatch) => {
    dispatch(patchDataPending());
    Axios.patch(data ? urlA : urlD, { is_active: datas })
      .then((res) => {
        dispatch(patchDataSuccess(res));
        console.log("ini respon", res);
        return res.data;
      })
      .catch((error) => {
        dispatch(patchDataError(error));
      });
  };
};

export default patchSwitch;
