import React, { useState, useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import {
  Grid,
  Typography,
  TextField,
  Switch,
  OutlinedInput,
} from "@material-ui/core";
import { fetchDetail } from "../redux/fetchData";
import { detailUpdateChange } from "../redux/actions";

function UpdatePage(props) {
  //manage props

  const { history, match } = props;
  let params = match.params.id;
  //redux
  const dispatch = useDispatch();

  //reducer fetching data
  const reducer = useSelector((state) => state);
  const res = reducer.detailReducer;

  //state init
  let dialogRef = useState(null);
  const [toggle, setToggle] = useState({ is_active: true });

  // lifecycle
  useEffect(() => {
    dispatch(fetchDetail(params));
  }, []);

  const handleCancel = () => {
    history.goBack();
  };

  const handleChangeName = (event) => {
    // dialogRef.focus();
    const { name, value } = event.target;
    console.log("hallo update name", event.target.value);

    dispatch({ type: "res", data: value });
  };

  const handleChangeComment = (event) => {
    console.log("hallo update comment", event.target.value);
  };
  const handleChangeSwitch = (event) => {
    console.log("is aktive =>", event.target.checked);
    // setToggle({
    //   ...toggle,
    //   is_active: !toggle.is_active,
    // });
  };

  return (
    <Grid container spacing={2}>
      <form noValidate autoComplete="off">
        <TextField onChange={handleChangeName} placeholder="ini testing"></TextField>
        <Grid item xs={12}>
          <Typography component="span">Name</Typography>
          <TextField
            onChange={handleChangeName}
            disabled={res.pending}
            name="username"
            //   ref={(el) => (dialogRef = el)}
            fullWidth
            key={`${Math.floor(Math.random() * 1000)}-min`}
            value={res.data.name}
          />
        </Grid>
        <Grid item xs={12}>
          <Typography component="span">Comment</Typography>
          <TextField
            onChange={handleChangeComment}
            name="comment"
            fullWidth
            multiline
            rows={4}
            key={`${Math.floor(Math.random() * 1000)}-min`}
            // value={res.pending ? "loading..." : res.data.comment}
          />
        </Grid>
        <Grid item xs={12}>
          <Typography component="span">Is active</Typography>
          <Switch
            name="is_active"
            disabled={res.pending}
            checked={
              !res.pending
                ? res.data.is_active === toggle.is_active
                  ? true
                  : false
                : false
            }
            onChange={handleChangeSwitch}
          />
        </Grid>
      </form>
    </Grid>
  );
}

export default UpdatePage;
