import React, { useState, useEffect, useRef } from "react";
import {
  Dialog,
  DialogContent,
  DialogTitle,
  Typography,
  DialogActions,
  Button,
  Grid,
  Switch,
  responsiveFontSizes,
  useFormControl,
} from "@material-ui/core";
import { useSelector, useDispatch } from "react-redux";
import { fetchDetailUpdateChange } from "../../redux/actions";
import patchSwitch from "../../redux/patchData";
import { fetchDetail } from "../../redux/fetchData";

//form
import InputTextField from "./Form/InputTextField";

function DialogEditUsers(props) {
  const { history, match } = props;
  let params = match.params.id;
  let inputRef = useRef(null);

  //redux
  let dispatch = useDispatch();
  useEffect(() => {
    dispatch(fetchDetail(params));
  }, []);

  //reducer fetching data
  const state = useSelector((state) => state);
  const res = state.detailReducer;
  const [toggle, settoggle] = useState({ is_active: false });
  console.log(res, state);

  const handleCancel = () => {
    history.goBack();
  };

  const handleChange = (id, status) => (props) => {
    // dispatch(patchSwitch(id, status));
    settoggle({
      is_active: !toggle.is_active,
    });
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    let data = res.data;
    data = {
      ...res.data,
      name: event.target[0].value,
      comment: event.target[2].value,
    };
    console.log("value submitan", res.data, data);
  };

  return (
    <Dialog open={true} aria-labelledby="form-dialog-edit-users" fullWidth>
      <form onSubmit={handleSubmit} ref={(e) => (inputRef = e)}>
        <DialogTitle>
          <Typography component="span" variant="h4">
            <b> Edit Data</b>
          </Typography>
        </DialogTitle>
        <DialogContent>
          <Grid container spacing={2}>
            <Grid item xs={12}>
              <Typography component="span">Name</Typography>
              <InputTextField
                name="username"
                fullWidth
                ref={(e) => (inputRef = e)}
                keys={`${Math.floor(Math.random() * 1000)}-min`}
                defaultValue={
                  res.pending === true ? "loading..." : res.data.name
                }
              />
            </Grid>
            <Grid item xs={12}>
              <Typography component="span">Comment</Typography>
              <InputTextField
                name="comment"
                fullWidth
                multiline
                rows={4}
                ref={(e) => (inputRef = e)}
                keys={`${Math.floor(Math.random() * 1000)}-min`}
                defaultValue={
                  res.pending === true ? "loading..." : res.data.comment
                }
              />
            </Grid>
            <Grid item xs={12}>
              <Typography component="span">Is active</Typography>
              <Switch
                name="detailCheckedA"
                value={true}
                // defaultValue={toggle.is_active}
                // checked={toggle.is_active}
                onChange={handleChange(params, res.data.is_active)}
              />
            </Grid>
          </Grid>
        </DialogContent>
        <DialogActions>
          <Button color="primary" onClick={handleCancel}>
            Cancel
          </Button>
          <Button color="primary" type="submit">
            Submit
          </Button>
        </DialogActions>
      </form>
    </Dialog>
  );
}

export default DialogEditUsers;
