import axios from "axios";

//action fetch data
export const FETCH_DATA_PENDING = "FETCH_DATA_PENDING";
export const FETCH_DATA_SUCCESS = "FETCH_DATA_SUCCESS";
export const FETCH_DATA_ERROR = "FETCH_DATA_ERROR";

export const fetchData = () => {
  return (dispatch) => axios.get("https://api.jokolodang.com/api/v1/payments")
      .then(response => {
           dispatch({type: FETCH_DATA_SUCCESS, data: response.data});
      })
  }

export const fetchDataPending = (pendding) => {
  //   { id, nama, harga } = data;
  return (dispatch, getState) => {
    dispatch({
      type: FETCH_DATA_PENDING,
      data: pendding,
    });
  };
};

export const fetchDataSuccess = (data) => {
  //   { id, nama, harga } = data;
  return (dispatch, getState) => {
    dispatch({
      type: FETCH_DATA_SUCCESS,
      data: data,
    });
  };
};

export const fetchDataError = (error) => {
  //   { id, nama, harga } = data;
  return (dispatch, getState) => {
    dispatch({
      type: FETCH_DATA_ERROR,
      data: error,
    });
  };
};

export const addWhiteList = (data) => {
  //   { id, nama, harga } = data;
  return {
    type: "ADD_WHITE_LIST",
    payload: data,
  };
};

export const removeWhiteList = (id) => {
  return {
    type: "REMOVE_WHITE_LIST",
    payload: id,
  };
};
