import Axios from "axios";
import { URL_MAIN } from "./url";
import {
  patchDataPending,
  patchDataSuccess,
  patchDataError,
} from "./actions";

const patchSwitch = (id, status) => {
  const body = {
    is_active: !status,
  };
  return (dispatch) => {
    dispatch(patchDataPending());
    Axios.put(`${URL_MAIN}/users/${id}`, body)
      .then((res) => {
        dispatch(patchDataSuccess(res.data));
        console.log("ini respon", res.data);
        return res.data;
      })
      .catch((error) => {
        dispatch(patchDataError(error));
      });
  };
};

export default patchSwitch;
