import { combineReducers } from "redux";
import {
  FETCH_DATA_PENDING,
  FETCH_DATA_SUCCESS,
  FETCH_DETAIL_SUCCESS,
  FETCH_DETAIL_PENDING,
  FETCH_DATA_ERROR,
  PATCH_DATA_PENDING,
  PATCH_DATA_SUCCESS,
  PATCH_DATA_ERROR,
} from "./actions";

//initial state
const initialState = {
  pending: false,
  data: [],
  error: null,
};

function dataReducer(state = initialState, action) {
  switch (action.type) {
    case FETCH_DATA_PENDING:
      return {
        ...state,
        pending: true,
      };
    case FETCH_DATA_SUCCESS:
      return {
        ...state,
        pending: false,
        data: action.data,
      };
    case FETCH_DETAIL_PENDING:
      return {
        ...state,
        pending: true,
      };
    case FETCH_DETAIL_SUCCESS:
      return {
        ...state,
        pending: false,
        detail: action.data,
      };
    case FETCH_DATA_ERROR:
      return {
        ...state,
        pending: false,
        error: action.error,
      };
    default:
      return state;
  }
}

function patchData(state = initialState, action) {
  switch (action.type) {
    case PATCH_DATA_PENDING:
      return {
        ...state,
        pending: true,
      };
    case PATCH_DATA_SUCCESS:
      return {
        ...state,
        pending: false,
        data: action.data,
      };
    case PATCH_DATA_ERROR:
      return {
        ...state,
        pending: false,
        error: action.error,
      };
    default:
      return state;
  }
}

const rootReducers = combineReducers({
  patchData: patchData,
  dataReducer: dataReducer,
});

export default rootReducers;
