import React, { useState, useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import fetchdData from "../redux/fetchData";
import patchSwitch from "../redux/patchData";
// import {dataReducer} from "../redux/reducers";
import axios from "axios";
import {
  Grid,
  Typography,
  Table,
  TableContainer,
  TableHead,
  TableBody,
  TableCell,
  Switch,
  TableRow,
  Button,
} from "@material-ui/core/";

function Home() {
  const dispatch = useDispatch();
  useEffect(() => {
    dispatch(fetchdData());
  }, []);

  // state init
  //   const [active, setactive] = useState({ statusSwitch: false });

  //reducer fetching data
  const datareducer = useSelector((state) => state.dataReducer);

  const handleChange = (id) => (props) => {
    console.log(id, props.target.value);
    // dispatch(patchSwitch(id));
  };

  const handleDelete = () => {
    return null;
  };

  //   console.log(datareducer.data.data, active);
  return (
    <Grid container item xs={12} justify="center" alignItems="center">
      <Grid item xs={10}>
        <TableContainer>
          <Table aria-label="simple-table">
            <TableHead>
              <TableRow>
                <TableCell>id</TableCell>
                <TableCell>name</TableCell>
                <TableCell>active</TableCell>
                <TableCell>delete</TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {datareducer &&
                datareducer.data &&
                datareducer.data.data &&
                datareducer.data.data.map((value, index) => (
                  <TableRow>
                    <TableCell>{value.id}</TableCell>
                    <TableCell>{value.name}</TableCell>
                    <TableCell>
                      <Switch
                        value={value.is_active}
                        // checked={active.statusSwitch}
                        onChange={handleChange(value.id)}
                        name="checkedA"
                      />
                    </TableCell>
                    <TableCell>
                      <Button onClick={handleDelete}>Delete</Button>
                    </TableCell>
                  </TableRow>
                ))}
            </TableBody>
          </Table>
        </TableContainer>
      </Grid>
    </Grid>
  );
}

export default Home;
