import { combineReducers } from "redux";
import {
  FETCH_DATA_PENDING,
  FETCH_DATA_SUCCESS,
  FETCH_DATA_ERROR,
  PATCH_DATA_PENDING,
  PATCH_DATA_SUCCESS,
  PATCH_DATA_ERROR,
} from "./actions";

//initial state
const initialState = {
  pending: false,
  data: [],
  error: null,
};

function dataReducer(state = initialState, action) {
  console.log(state, action);

  switch (action.type) {
    case FETCH_DATA_PENDING:
      return {
        ...state,
        pending: true,
      };
    case FETCH_DATA_SUCCESS:
      return {
        ...state,
        pending: false,
        data: action.data,
      };
    case FETCH_DATA_ERROR:
      return {
        ...state,
        pending: false,
        error: action.error,
      };
    default:
      return state;
  }
}

export const getData = state => state.data;
export const getDataPending = state => state.pending;
export const getDataError = state => state.error;

