import React, { useState, useEffect, useRef } from "react";
import {
  Dialog,
  DialogContent,
  DialogTitle,
  Typography,
  DialogActions,
  Button,
  Grid,
  Switch,
  responsiveFontSizes,
  useFormControl,
} from "@material-ui/core";
import { useSelector, useDispatch } from "react-redux";
import { updateDetailSwitch } from "../../redux/actions";
import patchSwitch from "../../redux/patchData";
import { fetchDetail } from "../../redux/fetchData";

//form
import InputTextField from "./Form/InputTextField";

function DialogEditUsers(props) {
  const { history, match } = props;
  let params = match.params.id;
  let inputName = useRef("input-name");
  let inputComment = useRef("input-commet");
  let inputSwitch = useRef("input-switch");

  //redux
  let dispatch = useDispatch();

  //reducer fetching data
  const state = useSelector((state) => state);
  const res = state.detailReducer;

  const [toggle, setToggle] = useState({ is_active: true });

  useEffect(() => {
    dispatch(fetchDetail(params));
    console.log("Ini kapan ke trigger sih ?");
  }, []);

  const handleCancel = () => {
    history.goBack();
  };

  const handleChange = (event) => {
    console.log(
      "is aktive =>",
      event.target.checked,
      toggle,
      res.data.is_active
    );
    setToggle({
      ...toggle,
      is_active: !toggle.is_active,
    });
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    let data = res.data;
    data = {
      ...res.data,
      name: event.target[0].value,
      comment: event.target[2].value,
      is_active: event.target[4].checked,
    };
    console.log("value submitan", res.data, data);
  };

  return (
    <Dialog open={true} aria-labelledby="form-dialog-edit-users" fullWidth>
      <form onSubmit={handleSubmit}>
        <DialogTitle>
          <Typography component="span" variant="h4">
            <b> Edit Data</b>
          </Typography>
        </DialogTitle>
        <DialogContent>
          <Grid container spacing={2}>
            <Grid item xs={12}>
              <Typography component="span">Name</Typography>
              <InputTextField
                changeValue
                name="username"
                fullWidth
                keys={`${Math.floor(Math.random() * 1000)}-min`}
                value={res.pending ? "loading..." : res.data.name}
              />
            </Grid>
            <Grid item xs={12}>
              <Typography component="span">Comment</Typography>
              <InputTextField
                changeValue
                name="comment"
                fullWidth
                multiline
                rows={4}
                keys={`${Math.floor(Math.random() * 1000)}-min`}
                value={res.pending ? "loading..." : res.data.comment}
              />
            </Grid>
            <Grid item xs={12}>
              <Typography component="span">Is active</Typography>
              <Switch
                name="is_active"
                disabled={res.pending}
                checked={
                  !res.pending
                    ? res.data.is_active === toggle.is_active
                      ? true
                      : false
                    : false
                }
                onChange={handleChange}
              />
            </Grid>
          </Grid>
        </DialogContent>
        <DialogActions>
          <Button color="primary" onClick={handleCancel}>
            Cancel
          </Button>
          <Button color="primary" type="submit">
            Submit
          </Button>
        </DialogActions>
      </form>
    </Dialog>
  );
}

export default DialogEditUsers;
