import React, { useState, useEffect } from "react";
import {
  Dialog,
  DialogContent,
  DialogTitle,
  Typography,
  DialogActions,
  Button,
  Grid,
  Switch,
  responsiveFontSizes,
} from "@material-ui/core";
import { useSelector, useDispatch } from "react-redux";
import { fetchDetail } from "../../redux/fetchData";
import patchSwitch from "../../redux/patchData";

//form
import InputTextField from "./Form/InputTextField";

function DialogEditUsers(props) {
  const { history, match } = props;
  let params = match.params.id;

  //redux
  let dispatch = useDispatch();

  useEffect(() => {
    dispatch(fetchDetail(params));
  }, []);

  //reducer fetching data
  const state = useSelector((state) => state);
  const res = state.detailReducer;
  const [toggle, settoggle] = useState({ is_active: false });
  console.log(res, state);

  const handleCancel = () => {
    history.goBack();
  };

  const handleChange = (id, status) => (props) => {
    // dispatch(patchSwitch(id, status));
    settoggle({
      is_active: !toggle.is_active,
    });
  };

  const handleChangeName = () => {
    console.log("hello change name", res);
  };

  const handleChangeComment = () => {
    console.log("hello change comment");
  };

  return (
    <Dialog open={true} aria-labelledby="form-dialog-edit-users" fullWidth>
      <DialogTitle>
        <Typography component="span" variant="h4">
          <b> Edit Data</b>
        </Typography>
      </DialogTitle>
      <DialogContent>
        <Grid container spacing={2}>
          <Grid item xs={12}>
            <Typography component="span">Name</Typography>
            <InputTextField
              changeValue={handleChangeName}
              fullWidth
              keys="name"
              value={res.pending === true ? "loading..." : res.data.name}
            />
          </Grid>
          <Grid item xs={12}>
            <Typography component="span">Comment</Typography>
            <InputTextField
              changeValue={handleChangeComment}
              fullWidth
              multiline
              rows={4}
              keys="comment"
              value={res.pending === true ? "loading..." : res.data.comment}
            />
          </Grid>
          <Grid item xs={12}>
            <Typography component="span">Is active</Typography>
            <Switch
              name="detailCheckedA"
              value={toggle.is_active}
              checked={toggle.is_active}
              onChange={handleChange(params, res.data.is_active)}
            />
          </Grid>
        </Grid>
      </DialogContent>
      <DialogActions>
        <Button color="primary" onClick={handleCancel}>
          Cancel
        </Button>
        <Button color="primary">Subscribe</Button>
      </DialogActions>
    </Dialog>
  );
}

export default DialogEditUsers;
